		}
	}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var facebookCheck = 'true';
		var twitterCheck = 'true';
		var youtubeCheck = 'true';
		var pinterestCheck = 'true';
		var googlePlusCheck = 'false';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
	    	if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}
		if(youtubeCheck == "true"){
		        var youtubeTarget = 'New Window';
		        if(youtubeTarget == "New Window"){
		        	youtubeTarget = "_blank";
		        } else {
		        	youtubeTarget = "_self";
		        }
		        $(".icn.youtube").removeClass("hidden").attr("target", youtubeTarget);
		        hasIcons = 1;
		}
		if(pinterestCheck == "true"){
		        var pinterestTarget = 'New Window';
		        if(pinterestTarget == "New Window"){
		        	pinterestTarget = "_blank";
		        } else {
		        	pinterestTarget = "_self";
		        }
		        $(".icn.pinterest").removeClass("hidden").attr("target", pinterestTarget);
		        hasIcons = 1;
		}
		if(googlePlusCheck == "true"){
		        var googlePlusTarget = 'New Window';
		        if(googlePlusTarget == "New Window"){
		        	googlePlusTarget = "_blank";
		        } else {
		        	googlePlusTarget = "_self";
		        }
		        $(".icn.googleplus").removeClass("hidden").attr("target", googlePlusTarget);
		        hasIcons = 1;
		}

		// SHOW THE ICONS IF THERE ARE ANY
		if(hasIcons == 1){
			$("#gb-mystart-left #gb-social-icons").removeClass("hidden");
			$("#gb-footer #gb-social-icons").removeClass("hidden");
			$("#gb-footer .gb-footer-two-bottom h2").removeClass("hidden");
			$("#gb-footer .gb-footer-icons h2").removeClass("hidden");
		}
	}

	function languageCheck() {
		if($(".hp").length){
			if(!readCookie("languageCheck")){
				var languageStructure =	'
' +
			                                	'
' +
				                                	'' +
			                                     	'' +
                                                  		'
' +
					                            '
Please choose your language:' +
					                            'English | ' +
					                            'Español | ' +
					                            'Tiếng Việt' +
                                                  		'' +
                                            		'';

		        	$("#language-check-outer").removeClass("hidden").append(languageStructure);      	

				$("#language-check-outer #language-choice a.english").click(function(e){
					e.preventDefault();
					$("#language-check-outer").addClass("hidden");	
				});
				$("#language-check-outer #language-choice a.spanish").click(function(e){
					$("#language-check-outer").addClass("hidden");	
				});
				$("#language-check-outer #language-choice a.viet").click(function(e){
					$("#language-check-outer").addClass("hidden");	
				});

				createCookie("languageCheck","", 1825);
			} else {
				$("#language-check-outer").addClass("hidden");	
			}
		}	
	}

	function createCookie(name, value, days) {
		var expires;

		if (days) {
			var date = new Date();
			date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
			expires = "; expires=" + date.toGMTString();
		} else {
			expires = "";
		}
		document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
	}

	function readCookie(name) {
		var nameEQ = escape(name) + "=";
		var ca = document.cookie.split(';');
		var giveBack = false;
		for (var i = 0; i < ca.length; i++) {
			var c = ca[i];
			while (c.charAt(0) === ' ') c = c.substring(1, c.length);
			if (c.indexOf(nameEQ) == 0) giveBack = true;
		}
		return giveBack;
	}

	function eraseCookie(name) {
		createCookie(name, "", -1);
	}

