	$(window).load(function() {
    	$("#cs-houston-alert .ui-widget.app.houston-alert .ui-articles li").each(function() {
        	var schools = jQuery.trim($(".cs-alert-check",this).text());
            if(schools != "") {
            	schools = schools.split(";");
                for(var s in schools) {
                	var check = schools[s].split(":");
                    if("hisd" == check[0] && check[1] == "true") {
                    	$(this).addClass(check[0] + "-" + check[1]);
                    }
                }
            }
        });
        $("#cs-houston-alert div.ui-widget.app.houston-alert .ui-articles li.hisd-true:eq(0)").css("border-top","0px none");
        if(!$("#sw-mystart-outer").length) {
          $("#module-content-180264").prependTo("#gb-page");
          $("#cs-houston-alert-outer").removeClass("hidden");
        } else {
          $("#sw-mystart-outer").after($("#module-content-180264"));
          $("#cs-houston-alert-outer").removeClass("hidden");
        }
    });

