	function adjustImgs(){
	var imgTarget = $('.news-summary .content .image > img');
	var imgTargetParent = $('.content .image');

	// Function check W:H of each image and adjusts accordingly
	imgTarget.each(function(){

	var imgW = $(this).width();
	var imgH = $(this).height();
	var imgWHr = imgW / imgH;

	var outW = imgTargetParent.width();
	var outH = imgTargetParent.height();
	var outWHr = outW / outH;

	if( outWHr >= imgWHr ){
	$(this).css({
	'width' : '100%',
	'height' : 'auto'
	});
	}
	else {
	$(this).css({
	'width' : 'auto',
	'height' : '100%'
	});
	}
	});
	}

