  });
  $('#showListButton-59464-891030550').click(function() {
    $('#perc-button-container-59464-891030550').hide();
    $('#calendar-59464-891030550').fullCalendar( 'destroy' );
    Perc.events['calendar-59464-891030550'] = jQuery.Deferred();
    jQuery.when(Perc.events['calendar-59464-891030550']).done( function( resolvedEvents ){
          percProcessEventList(resolvedEvents, maxResults);
    });
    Perc.range = 52;
    $('#perc-event-list-59464-891030550').fadeIn();
    $('#perc-event-list-59464-891030550 ul').empty();
    $('#calendar-59464-891030550').show();
    initializeCalendar();
      $('#calendar-59464-891030550').hide();
    });

  });
});

