        var globalIcons = [
          ["/cms/lib/TX01918059/Centricity/Template/GlobalAssets/icons/Eduphoria-exclamation-mark.png", "Eduphoria", "https://eduphoria.lubbockisd.org/AuthDistrict/Login?ReturnUrl=/default.aspx", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
