$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=7sHuRW74bI5AVNN8QLDIxg%3d%3d&SessionID=fd1908ff-9215-4e2c-90bf-d3ad1e2a7070&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+9%3a46%3a37+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.84.25';
