jQuery(document).ready(function($) {

    $('#articleSlider_140751').carousel({
            interval: 5000
    });

    $('#carousel-text_140751').html($('#slide-140751-content-0').html());

    //Handles the carousel thumbnails

    $('[id^=carousel-140751-selector-]').click( function(){
            var id_selector = $(this).attr("id");
            var id = id_selector.substr(id_selector.length -1);
            var id = parseInt(id);
            $('#articleSlider_140751').carousel(id);
    });

    // When the carousel slides, auto update the text
    $('#articleSlider_140751').on('slid.bs.carousel', function (e) {
        var id = $('#articleSlider_140751  .item.active').data('slide-number');
        $('#carousel-text_140751').html($('#slide-140751-content-'+id).html());
    });
});
//function to figure out if element is in viewport
$.fn.isInViewport = function() {
    var elementTop = $(this).offset().top;
    var elementBottom = elementTop + $(this).outerHeight();
    var viewportTop = $(window).scrollTop();
    var viewportBottom = viewportTop + $(window).height();
    return elementBottom > viewportTop && elementTop < viewportBottom;
};
//don't slide images in slideshow unless its in viewport
$(window).on('resize scroll', function() {
    $('.carousel.article-carousel-140751').each(function() {
        if ($(this).isInViewport()) {
            $('.carousel.article-carousel-140751').carousel('cycle');
        } else {

            $('.carousel.article-carousel-140751').carousel('pause');
        }
    });
});

// Normalize Carousel Heights - pass in Bootstrap Carousel items.
$.fn.carouselHeights = function() {

    var items = $(this), //grab all slides
        heights = [], //create empty array to store height values
        tallest; //create variable to make note of the tallest slide

    var normalizeHeights = function() {

        items.each(function() { //add heights to array
            heights.push($(this).height());
        });
        tallest = Math.max.apply(null, heights); //cache largest value

        items.each(function() {
            if (tallest > 500 || tallest == 0) {
                tallest = 500;
            }
            $(this).css('min-height',tallest + 'px');
            $('.maxHeight').css('max-height',tallest + 'px').css('width', '100vw').css('height', '100vh');
        });
    };

    normalizeHeights();

    $(window).on('resize orientationchange', function () {
        //reset vars
        tallest = 0;
        heights.length = 0;

        items.each(function() {
            $(this).css('min-height','0'); //reset min-height
        });
        normalizeHeights(); //run it again
    });

};
//set slideshow div to be as high as the tallest image to prevent page jumping
jQuery(function($){
    $(window).on('load', function(){
        $('#articleSlider_140751 .item').carouselHeights();
    });
});

