body.script:
jQuery(document).ready(function () {

    jQuery('#menu-header-left-menu li.schoolmenu').on('click', function () {
        var kk = jQuery("#annoucmentSection").offset().top;
        jQuery('html, body').animate({
            scrollTop: kk - 70
        }, 1500);
    });

    var save = jQuery('#leftMenu .widget').detach();
    jQuery('#leftMenu').empty().append(save);
    var save = jQuery('#rightMenu .widget').detach();
    jQuery('#rightMenu').empty().append(save);
    var save = jQuery('.headerTop .container .HeaderTopMenu').detach();
    jQuery('.headerTop .container .menutophead').empty().append(save);

    jQuery('body').on('.readmorelink', 'click', function () {
        console.log('hhhh');
        jQuery(this).closest('.readmoredata').show();
    });



});

