$(function() {
    $("#HomeNewsWrapper").on("rendered", function() {
        var ezRSSFeeds = $(this).find(".customrss__title"); //Find horizontal rss feeds
        var ezRSSDescriptions = $(this).find(".customrss__description");
        var tallest = 0;
        if (ezRSSFeeds.length > 0) {
            for (var i = 0; i < ezRSSFeeds.length; i++) { //Loop through each horizontal rss feed
                var $title = ezRSSFeeds[i];
                if ($title.clientHeight > tallest) tallest = $title.clientHeight;
            }
            ezRSSFeeds.css("height", tallest + "px");
            var tallest = 0;
            for (var i = 0; i < ezRSSDescriptions.length; i++) { //Loop through each horizontal rss feed
                var $description = ezRSSDescriptions[i];
                if ($description.clientHeight > tallest) tallest = $description.clientHeight;
            }
            ezRSSDescriptions.css("height", tallest + "px");
            $(this).ezScroller({
                direction: "horizontal",
                speed: 500,
                timeout: 15000,
                auto: false,
                nextButton: $("#next"),
                prevButton: $("#prev")
            });
        }
    });
    $("#HomeNewsWrapper").ezRender({
        template: "HomeNewsTemplate",
        url: "/page/rss/456",
        dataType: "xml",
        descriptionLength: 100,
        thumbnailWidth: 100,
        titleLength: 40,
        numberToDisplay: 10,
    });
    $("#testimonials .testimonials > div > div").ezPager({
        auto: true,
        timeout: 10000
    });
    ezAlert.init("/page/rss/4450", false);
});
