function GetRefresh_1080(kc, id, sd, pb) {
    var sv = document.getElementById("selSearch_1080").value;
    var formData = "kc=" + kc + "&id=" + id + "&sd=" + sd + "&pb=" + pb + "&sv=" + sv;
    $.ajax({
        type: 'POST',
        url: "/runtime/widgets/_CommPartial.aspx",
        data: formData,
        success: function (data) {
            $("#ContentPlaceHolder6_ContentRepeater6_ctl00_1_communicator_1").empty().html(data);
        }
    });
}

