function genBox() {
    if ($('.alert-box img.sub').length > 0) {
        $(".alert-box table a img:first-child").each(function(index) {
            var grabSrc = $(this).attr('src').split("_thumb").join(
                "");
            $('.alert-box-body').append(' ');
        });
    }
    $(window).load(function() {
        var alertHeight = $("#alert-box").height() / 2;
        var alertWidth = $("#alert-box").width() / 2;
        $("#alert-box").css('margin-top', -alertHeight);
        $("#alert-box").css('margin-left', -alertWidth);
        $('#alert-box .contents div br, #alert-box .contents br').last()
            .remove();
        $("#alert-box .close, #alert-box-overlay").click(function() {
            $("#alert-box, #alert-box-overlay").hide();
        });
        $("#alert-box , #alert-box-overlay").show();
        //TEMP FOR MOBILE ONLY
        if (window.location.href.indexOf("/m/") > -1) {
            $('.alert-box h1').text('Alert Message');
        }
    });
}
$(function() {
    if ($("#alert-box:contains('No Faculty or Staff are listed at this time.')").length > 0) {
        console.log('Alert Disabled')
    } else {
        if ($(".alert-box .contents script").length > 0) { 
			$('.alert-box .contents script:not([src^=http])').remove(); // Cross JSP Fix 
        }
        if ($("#alert-box .contents").html().replace(/\s/g, "").length >
            0) {  genBox(); 
        }
    }
});

