section.script:
document.addEventListener('DOMContentLoaded', function() {
	var options = {
		"slidesToShow": 2,
		"centerMode": true,
		"arrows": false,
		"autoplay": true,
		"autoplaySpeed": 1000,
		"mobileFirst": true,
		"responsive": [
			 {
				  "breakpoint": 767,
				  "settings": {
						"slidesToShow": 3
				  }
			 },
			 {
				  "breakpoint": 1023,
				  "settings": {
						"slidesToShow": 4
				  }
			 }
		]
	};

	$('#logos_list').slick(options);
});

body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
	topnavButton.classList.toggle("open");
}
