        $("li.sw-channel-item > a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();
                        $(this).parent().find(".sw-channel-dropdown li:first-child > a").focus();
