	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
            	if(!$(".gb-mystart.right #sw-mystart-search").length){
					$("#sw-mystart-search").appendTo(".gb-mystart.right");
                }   
			break;
			case "768":
            	if(!$("#gb-channel-list #sw-mystart-search").length){
					$("#sw-mystart-search").appendTo("#gb-channel-list");
                }
			break;
			case "640":	
            	if(!$("#gb-channel-list #sw-mystart-search").length){
					$("#sw-mystart-search").appendTo("#gb-channel-list");
                }
			break;
			case "480":
            	if(!$("#gb-channel-list #sw-mystart-search").length){
					$("#sw-mystart-search").appendTo("#gb-channel-list");
                }
				appView();
			break;
			case "320":
            	if(!$("#gb-channel-list #sw-mystart-search").length){
					$("#sw-mystart-search").appendTo("#gb-channel-list");
                }
				appView();
			break;
		}
	}

