	function appView(){
		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$(".hp.column div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$(".hp.column div.ui-widget-header").not(".hp.column.three div.ui-widget-header").not(".hp.column.four div.ui-widget-header").not(".hp.column.five div.ui-widget-header").off("click").click(function(){
			if(!$(this).parent().hasClass("active")){

            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
		});
        $(".hp.column.three #sw-content-container3 > div:first-child .ui-widget.app .ui-widget-header").off("click").click(function(){
			if(!$(this).parent().parent().parent().parent().parent().parent().hasClass("active")){
                $(this).parent().parent().parent().parent().parent().parent().addClass("active");
            } else {
                $(this).parent().parent().parent().parent().parent().parent().removeClass("active");
            }
		});
        $(".hp.column.four #sw-content-container4 > div:first-child .ui-widget.app .ui-widget-header").off("click").click(function(){
			if(!$(this).parent().parent().parent().parent().parent().parent().hasClass("active")){
                $(this).parent().parent().parent().parent().parent().parent().addClass("active");
            } else {
                $(this).parent().parent().parent().parent().parent().parent().removeClass("active");
            }
		});
        $(".hp.column.five #sw-content-container5 > div:first-child .ui-widget.app .ui-widget-header").off("click").click(function(){
			if(!$(this).parent().parent().parent().parent().parent().parent().hasClass("active")){
                $(this).parent().parent().parent().parent().parent().parent().addClass("active");
            } else {
                $(this).parent().parent().parent().parent().parent().parent().removeClass("active");
            }
		});
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }    

