div.script:
$(function() {
var scr = document.getElementById('script_1');
var spn = document.getElementById('script_node_1');
var el = spn.parentNode;
var f = function() {
if (scr && scr.parentNode) scr.parentNode.removeChild(scr);
if (spn && spn.parentNode) spn.parentNode.removeChild(spn);

	var popupAlertItems = $('#alert-bar .item.do-alert-modal');
	var items = []
	if (/resetAlreadyShown/.test(location.href))
	{
		var remove = [];
		for (var i = 0; i < window.localStorage.length; i++) {
			var k = window.localStorage.key(i);
			if (/^alertItemShown_/.test(k))
				remove.push(k);
		}
		for (var i = 0; i < remove.length; i++)
			window.localStorage.removeItem(remove[i]);
	}
	popupAlertItems.each(function() {
		var k = "alertItemShown_" + this.getAttribute('data-alert-id');
		if (window.localStorage.getItem(k))
			return;
		window.localStorage.setItem(k, true);
		items.push(this);
	});


	if (items.length) {
		for (var i = 0; i < items.length; i++) {
			$('#modal-alert .modal-body').append($(items[i]).find('.carousel-caption').html());
		}
		$('#modal-alert').modal('show');
	}
};
f.apply(el, []);
});

function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', includedLanguages: 'ar,bs,de,en,es,fr,hi,hr,ko,lo,ru,sw,te,th,tl,tr,vi,zh-CN,zh-TW', gaTrack: true, gaId: 'UA-49581714-1'}, 'google_translate_element');
  $('#google_translate_element select').attr('name', 'translate').attr('title', 'Select a Language');
  // This is for the benefit of the accessibility validator.
  $('link[charset]').each(function() { this.removeAttribute('charset'); });
}

var s = document.location.search.substring(1).split('&');
for (var i = 0; i < s.length; i++) {
  if (s[i].substring(0, 2) == 'q=') $('#searchInput').val(decodeURIComponent(s[i].substring(2)));
}

