	var tabsLeftResize = function(location){
		$(location).find('.nav-tabs-left').css({ height: '' });
		$(location).find('.nav-tabs-left li a').css({ height: '', 'line-height': ''});
		$(location).find('.tab-content').css({ height: '' });

		$(location).find('.tab-pane').each(function() {
			newH = Math.max(newH, $(this).outerHeight()); 
		});
		$(location).find('.tab-content').css({ height: newH });

		navItemH = newH / visibleLinks;
		$(self).find('.nav-tabs-left').css('height', newH);
		$(self).find('.nav-tabs-left li a').css({ height: navItemH + 'px', 'line-height': navItemH + 'px', 'padding-top': '0', 'padding-bottom': '0' });
	}	

	//Mega menu height size
	var megaMenuGridResize = function(location){
		if($(location).hasClass('mega-menu-grid')){
			$(location).find('.dropdown-menu [class*="col-"]').css('height', '');
			var bh = 0;
			$(location).find('.dropdown-menu [class*="col-"]').each(function() {
				bh = Math.max(bh, $(this).height()); 
			});
			$(location).find('.dropdown-menu [class*="col-"]').css('height', bh + 'px');
		}
	}	
	var gridThumbnailResize = function(){				
		var H = 0;
		$('.grid-item-thumbnail .media-body').css('height', '');
		$('.grid-item-thumbnail .media-body').each(function() {
			H = Math.max(H, $(this).outerHeight());
		});
		$('.grid-item-thumbnail').each(function() {
			$(this).children('.media-body').css('height', H);
		});
	}

