		var videoResize = function(){
			$('.gallery iframe, .gallery video').each(function() {
				var $el = $(this);
				if (!$el.attr('data-aspectRatio')) {
					$el.attr('data-max-width', this.width).attr('data-max-height', this.height);
					$el.attr('data-aspectRatio', this.height / this.width).removeAttr('height').removeAttr('width');
				}
				var $rv = $el.parents('.modal-gallery .modal-body').eq(0);
				var newWidth = Math.min($($rv).width(), $el.attr('data-max-width'));
				$el.width(newWidth).height(newWidth * $el.attr('data-aspectRatio'));
			});
		}

		$(window).on('resize', function() {
			$('.modal-gallery').on('shown.bs.modal', function (e) {
				videoResize();
			});  
			videoResize();
		});
		$(function() {
			$(window).trigger('resize');

		});
	});


