	var mf1 = function(mq) {
		if (!mq.matches) return;
		var isTabClick = false;
		$('#navbarBottom .dropdown .nav-tabs a').on('click', function() {
			isTabClick = true;
		})
		$('.dropdown').off('mouseenter mouseleave').on('mouseenter', function(){
			$(this).find('.nav-tabs-left li a:first').tab('show');
			megaMenuGridResize(this);
			//tabsLeftResize(this);
			if($(this).parents().hasClass('nav-dropdown-hover')){
				$(this).addClass('open');
				if ($(this).is('.active')) $(this).addClass('keep-active');
				megaMenuGridResize(this);
			}
		}).on('mouseleave', function() {
			if($(this).parents().hasClass('nav-dropdown-hover')){
				if ($(this).is('.keep-active')) $(this).addClass('active').removeClass('keep-active');
				$(this).removeClass('open active');
			}
		}).on('shown.bs.dropdown', function() {
			$(this).find('.dropdown-menu a[tabindex]').each(function() {
				$(this).attr('data-tabindex', $(this).attr('tabindex')).removeAttr('tabindex');
			});
			$(this).find('.dropdown-menu a:first').focus();
		}).on('hide.bs.dropdown', function(event) {
			if (isTabClick) {
				isTabClick = false;
				event.preventDefault();
				return false;
			}
		}).on('hidden.bs.dropdown', function() {
			$(this).find('.dropdown-menu a[data-tabindex]').each(function() {
				$(this).attr('tabindex', $(this).attr('data-tabindex')).removeAttr('data-tabindex');
			});
			$(this).find('.dropdown-toggle').focus();
		});

		$('.nav-tabs-left a').off('mouseenter mouseleave').on('mouseenter', function(e){
			$(this).tab('show');
		});

		gridThumbnailResize();

	}

	var mf2 = function(mq) {
		if (!mq.matches) return;

		$('.dropdown').off('mouseenter mouseleave shown.bs.dropdown hide.bs.dropdown hidden.bs.dropdown');
		$('.dropdown li').removeClass('open active keep-active');
		$('.nav-tabs-left a').off('mouseenter mouseleave');
		$('.grid-item-thumbnail .media-body').css('height', '');
	}

	var mf3 = function(mq) {
		if (!mq.matches) return;

		gridThumbnailResize();
	}

	var mm1 = Media.match("(max-width:767px)");
	var mm2 = Media.match("(min-width: 768px)");
	var mm3 = Media.match("(min-width: 980px)");
	var mm4 = Media.match("(max-width: 979px)");

	$(window).load(function() {
		if (mm1.matches) mf2(mm1);
		if (mm2.matches) mf1(mm2);
		if (mm3.matches) mf3(mm3);
		if (mm4.matches) mf3(mm4);
	});

	if (mm1.matches) mf2(mm1);
	if (mm2.matches) mf1(mm2);
	if (mm3.matches) mf3(mm3);
	if (mm4.matches) mf3(mm4);

	mm1.addListener(mf2, true);
	mm2.addListener(mf1, true);
	mm3.addListener(mf3, true);
	mm4.addListener(mf3, true);

