        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/WA01919490/Centricity/Template/GlobalAssets/images///logos/Website_DO129x87px.png");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                    $("#gb-logo-mobile").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                    $("#gb-logo-mobile").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('Snoqualmie Valley');
            var siteNameTwo = jQuery.trim('School District');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Snoqualmie Valley School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
                $("#gb-sitename-mobile").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
                $("#gb-sitename-mobile").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
                $("#gb-sitename-mobile").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
                $("#gb-sitename-mobile").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
        },
        "ChannelBar": function() {
            //USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").first().addClass("first");
            //ADD LAST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").last().addClass("last");

            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

                // LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();
                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

            });

            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
            $("ul.sw-channel-list li.sw-channel-item").addClass("transition");
        },

        "StickyChannelBar": function() {
        	var windowWidth = $(window).width();
        	if(windowWidth >= 1024){
                var headerHeight = $("#gb-header-outer").height();
                $("#hp-slideshow-outer").css("margin-top", headerHeight);

                if ($(window).scrollTop() > headerHeight) {

                    if(!$("#gb-header-outer").hasClass("fixed")){
                        $("#gb-header-outer").addClass("fixed");
                        $("#hp-slideshow-outer").css("margin-top",headerHeight);
                        $("#gb-header-outer").css("top", "-" + $("#gb-header-outer").height() + "px").stop().animate({
                                            top: "0px"
                                        }, 400);



                    } 

                    if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                            if($(".wcm-controls").hasClass("wcm-stuck")) {
                                $(".wcm-controls").css("top", "60px");
                            }

                        }  



                } else {
                    if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {

                        $(".wcm-controls").css("margin-top", "0px");
                    }
                    $("#gb-header-outer").removeClass("fixed");
                    $("#hp-slideshow-outer").css("margin-top","0px");
                }

                /*$("#sw-maincontent").css({
                                "display": "block",
                                "position": "relative",
                                "top": "-" + $("#gb-header-outer").outerHeight(true) + "px"
                            });*/
       		}                     
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

           //RENAME FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            //REPLACE MORE-LINK TEXT
            $("a.more-link span").text("view all");

            //AUTO-FOCUS UERSNAME SIGIN FIELD
            $("#swsignin-txt-username").focus();

            //CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //ADD SIGNED OUT BREADCRUMB
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            //ADD SIGN IN BREADCRUMB
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

                //REGISTER BREADCRUMB
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            //SEARCH RESULTS BREADCRUMB
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            //CHANGE TEXT OF CALENDAR LINK
            $("a.view-calendar-link").text("view full calendar");

            //LIMIT EVENTS ON HOMEPAGE - REMOVE IF NOT NEEDED
            $("#hp-region-three .upcomingevents li:gt(4)").remove();

            //MOVE THE VIEW ALL TO THE HEADER
            if(!$("div.ui-widget.app.headlines .ui-widget-header .more-link").length){
                $("div.ui-widget.app.headlines").each(function(appIndex, app){
                        if( $('.more-link', app).length > 0 ){
                            $('.more-link', app).appendTo( $('.ui-widget-header', app) );
                        }   
                });
            }

            //SCROLL FUNCTION FOR BACK TO TOP BUTTON
            $("#gb-back-to-top").click(function(e){
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                    $("li.sw-channel-item:first-child a").focus();
            });

            //CHANGE RSS HTML
            $(".app-level-social-rss a").empty().addClass("icon-font-rss").attr("aria-label","Subscribe to RSS feed");

            //MOVE RSS INTO THE DETAIL
            /*if(!$(".hp-row.two div.ui-widget.app .ui-widget-detail .app-level-social-rss").length){
                $(".hp-row.two div.ui-widget.app").each(function(appIndex, app){
                        if( $('.ui-widget-footer .app-level-social-rss', app).length > 0 ){
                            $('.ui-widget-footer .app-level-social-rss', app).appendTo( $('.ui-widget-detail', app) );
                        }   
                });
            }*/

            if(!$(".hp-row.two div.ui-widget.app .ui-widget-footer .ui-read-more").length){
                $(".hp-row.two div.ui-widget.app").each(function(appIndex, app){
                        if( $('.ui-widget-detail .ui-read-more', app).length > 0 ){
                            $('.ui-widget-detail .ui-read-more', app).prependTo( $('.ui-widget-footer', app) );
                        }   
                });
            }

            $("#gb-back-to-top").keydown(function(e){
                if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                    $("li.sw-channel-item:first-child a").focus();
                }
            });

            $(".ui-widget.app.headlines .ui-articles > li").hover(function(){
            	$(this).addClass("hover");
            }, function(){
                $(this).removeClass("hover");
            });

            $(".ui-widget.app.headlines .ui-articles > li .ui-article-title a").focus(function(){
            	$(this).parent().parent().parent().addClass("hover");
            });
            $(".ui-widget.app.headlines .ui-articles > li .ui-article-title a").focusout(function(){
                $(this).parent().parent().parent().removeClass("hover");
            });
            $("div.ui-widget.app.navigation li a").focus(function(){
            	$(this).parent().addClass("focus");
            });
            $("div.ui-widget.app.navigation li a").focusout(function(){
                $(this).parent().removeClass("focus");
            });

            //SLIDESHOW ARROW SCROLL
            $(".scroll-down span").click(function(){
                $("html, body").animate({scrollTop: ($("#hp-row-one-outer").offset().top - 150)}, "slow"); 
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            //SPOTLIGHT SECTION
            var spotlightText = "Welcome to the Snoqualmie Valley School District. We are proud to partner with a community that values education and supports the District’s mission to prepare all students for college, career and citizenship. Located 30 miles east of Seattle in the scenic Cascade foothills, SVSD covers 400+ square miles, serving families in Snoqualmie, North Bend, Fall City and surrounding areas. Our 10 award-winning schools offer 7,200 students a strong academic foundation, coupled with enrichment activities and support to help students discover their passions for life-long learning and success after graduation. ";
