        "Search": function() {
            //CUSTOM MYSTART SEARCH PLACEHOLDER
            //SEARCH TEXT
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
            $("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            });
            $("div#sw-mystart-search input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            $("div#sw-mystart-search input#sw-search-input").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=8&SearchString=" + $("#gb-search-input").val();
                }
            });
        },


