        "Slideshow": function() {
            if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1500,
                    "imageHeight" : 915,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : ["image"], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "slide", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : true,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {// props.element, props.recordIndex, props.mmgRecords

                    		var imageSrc = props.mmgRecords[props.recordIndex].imageSrc;
                    		var imageAlt = props.mmgRecords[props.recordIndex].imageAltText;
        					var smallImageStructure = '
';
                            $(".mmg-slide[data-record-index='" + props.recordIndex + "'] .mmg-image", props.element).prepend(smallImageStructure);

                            var windowHeight = $(window).height();
                            var headerHeight = $("#gb-header-outer").height();
                            var globalIconHeight = $("#gb-global-icons-outer").height();
                            var smallImageHeight = windowHeight - headerHeight - globalIconHeight;
                            $(".mmg-slide[data-record-index='" + props.recordIndex + "'] .mmg-image-small").css("height",smallImageHeight);
                    }, 
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {


                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) { 

                    	$(".mmg-control").wrapAll("
");

                    	var windowWidth = $(window).width();
                        var contentWidth = $("#gb-global-icons").width();
                        var descOffset = (windowWidth - contentWidth)/2;
                        var controlWidth = $("#hp-slideshow .mmg-container .mmg-control-outer").width() + 100;
                        var controlOffset = descOffset + 400 - controlWidth;
                    	$("#hp-slideshow .mmg-container .mmg-description").css("right",descOffset);
                        $("#hp-slideshow .mmg-container .mmg-control-outer").css("right",controlOffset);


                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {} // props.element, props.mmgRecords
                });
            }
        },
