                $("div#hp-tabs div#sw-content-container4 .tab-button:eq(0)").addClass("first");

                $("#sw-content-container4 .tabs-container .tab-button").each(function(){
                	var tabText = $(".tab-right",this).text().toLowerCase();

                	switch(tabText) {
                    case "facebook":
                    	$(".tab-right",this).html('');
                    break;
                    case "twitter": 
                    	$(".tab-right",this).html('');
                    break;
                    case "youtube":
                    	$(".tab-right",this).html('');
                    break;
                    case "instagram": 
                    	$(".tab-right",this).html('');
                    break;
                    case "vimeo":
                    	$(".tab-right",this).html('');
                    break;
                }

                });
			}
