        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function(){
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                var countLi = 0;
                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                    if($('.upcoming-column.left h1', this).length){

                    	var newDateTime = $('.upcoming-column.left h1', this).html().split("");
                        var newDate = '';
                        var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                        //add a zero to the date if it is a single digit
                        if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                       //add in the month
                        newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                        //if we have the day of the week make sure to include it.
                        if( dateIndex > 1 ){ 
                            newDate += newDateTime[0] + "";
                        }

                         //wrap the date in a new tag
                        newDate += '' + newDateTime[dateIndex] + '';

                        //append the date and month back into their columns
                        $('.upcoming-column.left h1', this).html(newDate);

                        //$(".joel-month",this).next(".joel-day",this).andSelf().wrapAll('');

                        //add an ALL DAY label if no time was given
                        $('.upcoming-column.right .ui-article-description', this).each(function(){
                            if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                                $(this).prepend('ALL DAY');
                            }
                        });

                    } 
                });
                $('.ui-widget.app.upcomingevents .ui-widget-detail .ui-article').each(function(){
                    $(".upcoming-column.left .joel-day", this).prependTo($(".upcoming-column.right",this));
                });
            }

            $(".ui-widget.app.upcomingevents").each(function(){

            	var months = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
				var monthsLong = ["January","February","March","April","May","June","July","August","September","October","November","December"];

            	var fullCalLink = $(".view-calendar-link",this).attr("href"); //GRAB VIEW CALENDAR LINK
                if($(".ui-articles li:eq(0) .ui-article-description:eq(0) .sw-calendar-block-title > a",this).length){
                    var eventLink = $(".ui-articles li:eq(0) .ui-article-description:eq(0) .sw-calendar-block-title > a",this).attr("href"); //GRAB LINK FROM FIRST EVENT IN THE APP
                    var eventLinkSplit = eventLink.split("#"); //SPLIT THAT LINK ON THE #
                    var keySplit = eventLinkSplit[1].split("/"); // SPLIT SECOND ITEM IN THE ARRAY TO GET THE CALENDAR KEY
                    var calendarKey = keySplit[0]; //THIS IS THE CALENDAR KEY
                }

            //IF THERE ARE MORE THAN 4 EVENTS ON THE SAME DAY, ADD BUTTON
                $('.upcomingevents li').each(function() {
                    var eventCount = $(this).find('.ui-article-description').length;

                   	var ariaMonth = monthsLong[months.indexOf($(".joel-month", this).text())];
                    var eventMonth = $(".joel-month", this);
                    var eventDay = $(".upcoming-column.right .joel-day", this);
                    var eventDate = $.trim($(".matt-date", this).text());
                    //if(eventDate.length == 1) eventDate = "0" + eventDate;

                    var ariaDate = eventDate;
                    switch(eventDate) {
                        case "01": ariaDate = "First"; break;
                        case "02": ariaDate = "Second"; break;
                        case "03": ariaDate = "Third"; break;
                        case "04": ariaDate = "Fourth"; break;
                        case "05": ariaDate = "Fifth"; break;
                        case "06": ariaDate = "Sixth"; break;
                        case "07": ariaDate = "Seventh"; break;
                        case "08": ariaDate = "Eighth"; break;
                        case "09": ariaDate = "Ninth"; break;
                        case "10": ariaDate = "Tenth"; break;
                        case "11": ariaDate = "Eleventh"; break;
                        case "12": ariaDate = "Twelth"; break;
                        case "13": ariaDate = "Thirteenth"; break;
                        case "14": ariaDate = "Fourteenth"; break;
                        case "15": ariaDate = "Fifteenth"; break;
                        case "16": ariaDate = "Sixteenth"; break;
                        case "17": ariaDate = "Seventeenth"; break;
                        case "18": ariaDate = "Eighteenth"; break;
                        case "19": ariaDate = "Nineteenth"; break;
                        case "20": ariaDate = "Twentieth"; break;
                        case "21": ariaDate = "Twenty-first"; break;
                        case "22": ariaDate = "Twenty-second"; break;
                        case "23": ariaDate = "Twenty-third"; break;
                        case "24": ariaDate = "Twenty-fourth"; break;
                        case "25": ariaDate = "Twenty-fifth"; break;
                        case "26": ariaDate = "Twenty-sixth";  break;
                        case "27": ariaDate = "Twenty-seventh"; break;
                        case "28": ariaDate = "Twenty-eighth"; break;
                        case "29": ariaDate = "Twenty-ninth"; break;
                        case "30": ariaDate = "Thirtieth"; break;
                        case "31": ariaDate = "Thirty-first"; break;
                    }

                    if(eventCount > 4) {
                      $('.ui-article-description:gt(3)',this).addClass("extra");

                      var currentEventLink = $(".ui-article-description:eq(0) .sw-calendar-block-title > a",this).attr("href"); //GRAB THE LINK FROM THE FIRST EVENT ON THAT DAY
                      var currentEventLinkSplit = currentEventLink.split("#"); //SPLIT THAT LINK ON THE #
                      var currentKeySplit = currentEventLinkSplit[1].split("/"); //SPLIT THE SECOND ITEM IN THE ARRAY TO GET THE DATE IN THE PROPER FORMAT
                      var currentDate = currentKeySplit[1]; //THIS IS THE CURRENT DATE IN THE PROPER FORMAT
                      var moreCalLink = fullCalLink + "#" + calendarKey + "/" + currentDate + "/day"; //BUILD THE LINK TO THE PROPER DAYS EVENTS ON THE CALENDAR PAGE

                      $(this).append('MORE').addClass('more');

                   }
                });
            });    
        }
