	function googleTranslateElementInit() {
			new google.translate.TranslateElement({
			
			pageLanguage: 'en',
			//layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
			autoDisplay: false
		}, 'google_translate_element');
		$('.goog-te-gadget-simple').css('font-size', '10px');
		$('.goog-te-menu-frame').attr('title', 'Google Translator');
		// begin accessibility compliance, requires jQuery
        $('img.goog-te-gadget-icon').attr('alt','Google Translate');
        $('div#goog-gt-tt div.logo img').attr('alt','translate');
        $('div#goog-gt-tt .original-text').css('text-align','left');
        $('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
		$('.goog-te-combo').attr('title','Select A Language');
		//$("html").attr("lang", pageLanguage);
        // end accessibility compliance
	}

link.script:
$(window).ready(function() {
	homePageFunctions();
   	$('.main-slider').bxSlider({
		mode:'horizontal',
		speed:500,
		pause:'9000',
		slideSelector: 'div.slide',
		adaptiveHeight: true,
		video: true,
		touchEnabled: true,
		pager:true,
		pagerType: 'full',
		pagerCustom: '.slideNav',
		auto: true,
		pause:'9000',
		autoHover: true,
		onSliderLoad: function(currentIndex) {     
			$('.main-slider').children().eq(currentIndex + 1).addClass('active-slide');
		},
		onSlideAfter: function($slideElement){
			$('.main-slider').children().removeClass('active-slide');
			$slideElement.addClass('active-slide');
		}
	});
	$('#eventSection ul').bxSlider({
		mode: 'vertical',
		slideSelector:'li.upcoming_events_above',
		adaptiveHeight: true,
		touchEnabled: true,
		pager:true,
		pagerType: 'full',
		minSlides: 4
	});
});
