	function switchDip_511707(num,pause){
		clearTimeout(slideshowTimer_511707);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_511707();
		var speed = slideshowMotionSpeed_511707();
		var currentNum = document.getElementById('currentNum_511707').value;
		var totalDips = document.getElementById('totalDips_511707').value;
		var video = document.getElementById('video_511707').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_511707(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_511707' + nums).length){
				$('#nav_511707' + nums).removeClass('on');
			}
			
			if($('#nav_li_511707' + nums).length){
				$('#nav_li_511707' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_511707' + nums).length){
					$('#videoShell_511707' + nums).hide();
				}
				$('#dipImage_511707' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_511707' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_511707' + nums).length){
				$('#videoShell_511707' + nums).fadeOut(speed);
			}
			if($('#dipContent_511707' + nums).length){
				$('#dipContent_511707' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_511707' + nums).length){
				$('#nav_511707' + nums).addClass('on');
			}
			
			if($('#nav_li_511707' + nums).length){
				$('#nav_li_511707' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_511707' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_511707' + nums).length){
					$('#videoShell_511707' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_511707' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_511707' + nums).length){
					$('#videoShell_511707' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_511707' + nums).length){
				$('#dipContent_511707' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_511707(timer);
			document.getElementById('currentNum_511707').value = nums;
		}
	}
	
	function slideshowMotion_511707(){
		if($('#slideshowMotion_511707').length){
			slideshowMotion = document.getElementById('slideshowMotion_511707').value;
		}else{
