		var totalDips = document.getElementById('totalDips_346177').value;
		var video = document.getElementById('video_346177').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_346177(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
