			}
			
			if($('#videoShell_346177' + nums).length){
				$('#videoShell_346177' + nums).fadeOut(speed);
			}
			if($('#dipContent_346177' + nums).length){
				$('#dipContent_346177' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_346177' + nums).length){
				$('#nav_346177' + nums).addClass('on');
