{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
        '
{{{_highlightResult.description.value}}}'+
      ''+
    '';

    var staffTemplate = '
' +
      '
' +
        ''+
      '' +
      '
