    search_staff.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_page.addWidget({
      init: function(args) {
        args.helper.addDisjunctiveFacetRefinement(
          'organization', gon.organization
        );
      }
    });

    search_live_feed.addWidget({
      init: function(args) {
        args.helper.addDisjunctiveFacetRefinement(
          'organization', gon.organization
