	});
	$('.announce-slick').slick({
	  dots: true,
	  infinite: true,
	  adaptiveHeight: true,
	  autoplay: true,
	  autoplaySpeed: 10000,
	  arrows: false,
	  appendDots: '.annNav'
	});
	$('.event-slick').slick({
