			{
			  breakpoint: 1200,
			  settings: {
				slidesToShow: 4,
				slidesToScroll: 1,
				infinite: true
			  }
			},
			{
			  breakpoint: 1040,
			  settings: {
				slidesToShow: 3,
				slidesToScroll: 1,
				infinite: true
			  }
			},
			{
			  breakpoint: 800,
			  settings: {
