	function switchDip_352449(num,pause){
		clearTimeout(slideshowTimer_352449);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_352449();
		var speed = slideshowMotionSpeed_352449();
		var currentNum = document.getElementById('currentNum_352449').value;
		var totalDips = document.getElementById('totalDips_352449').value;
		var video = document.getElementById('video_352449').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_352449(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_352449' + nums).length){
				$('#nav_352449' + nums).removeClass('on');
			}
			
			if($('#nav_li_352449' + nums).length){
				$('#nav_li_352449' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_352449' + nums).length){
					$('#videoShell_352449' + nums).hide();
				}
				$('#dipImage_352449' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_352449' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_352449' + nums).length){
				$('#videoShell_352449' + nums).fadeOut(speed);
			}
			if($('#dipContent_352449' + nums).length){
				$('#dipContent_352449' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_352449' + nums).length){
				$('#nav_352449' + nums).addClass('on');
			}
			
			if($('#nav_li_352449' + nums).length){
				$('#nav_li_352449' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_352449' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_352449' + nums).length){
					$('#videoShell_352449' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_352449' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_352449' + nums).length){
					$('#videoShell_352449' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_352449' + nums).length){
				$('#dipContent_352449' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_352449(timer);
			document.getElementById('currentNum_352449').value = nums;
		}
	}
	
	function slideshowMotion_352449(){
		if($('#slideshowMotion_352449').length){
			slideshowMotion = document.getElementById('slideshowMotion_352449').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
