		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker10 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker10 a.ui-datepicker-prev').click();
	    		handlePrevClick10();
	    	}
	    });
	    $('#datepicker10 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker10 a.ui-datepicker-next').click();
	    		handleNextClick10();
	    	}
	    });


		function handlePrevClick10(){
			setTimeout(function () {
				triggerClick10();
			    $('#datepicker10').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick10(){
			setTimeout(function () {
				triggerClick10();
			    $('#datepicker10').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick10(){
			prev = $('#datepicker10').find('.ui-datepicker-prev');
			next = $('#datepicker10').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick10);
			$(prev).on('click', handlePrevClick10);
		}

		};



