link.script:
$(document).ready(function() {
	homePageFunctions();
   
   $('#slideshowShell .content .description a').focus(function(){
		$('img', this).attr('src', function(){
		 	this.src = '//www.riverdale.k12.wi.us/custom/images/homepage/dip_nav/btn_circle_arrow_over.gif';
		});
	});
		
	$('#slideshowShell .content .description a').focusout(function(){
		$('img', this).attr('src', function(){
		 	this.src = '//www.riverdale.k12.wi.us/custom/images/homepage/dip_nav/btn_circle_arrow_off.gif';
		});
	});  
		
	$('.announcementsItem').focus(function(){
		$('.annOverlay').fadeOut(100);
		$('.annOverlay',this).fadeIn(100);
	 });
	 
	 $('#middleSectionShell').focus(function(){
		$('.annOverlay').fadeOut(100);
	 });
});

div.script:
                function googleTranslateElementInit() {
                        new google.translate.TranslateElement({  
                                  			
                        pageLanguage: 'en',
                        layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
                        autoDisplay: false
                    }, 'google_translate_element');
                    $('.goog-te-gadget-simple').css('font-size', '10px');
					$('.goog-te-menu-frame').attr('title', 'Google Translator');
					// begin accessibility compliance, requires jQuery
					$('img.goog-te-gadget-icon').attr('alt','');
					$('div#goog-gt-tt div.logo img').attr('alt','translate');
					$('div#goog-gt-tt .original-text').css('text-align','left');
					$('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
					$('.goog-te-combo').attr('title','Select A Language');
                }

function event_view_CMS494396(eventid) {
	windowprops = "top=0,left=0,width=600,height=500,resizable=yes,scrollbars=yes,menubar=yes";
	url = "/pro/event_view.cfm?memberid=124&eventid=" + eventid;
	window.open(url, "event"+eventid, windowprops);
}
