	function switchDip_409419(num,pause){
		clearTimeout(slideshowTimer_409419);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_409419();
		var speed = slideshowMotionSpeed_409419();
		var currentNum = document.getElementById('currentNum_409419').value;
		var totalDips = document.getElementById('totalDips_409419').value;
		var video = document.getElementById('video_409419').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_409419(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_409419' + nums).length){
				$('#nav_409419' + nums).removeClass('on');
			}
			
			if($('#nav_li_409419' + nums).length){
				$('#nav_li_409419' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_409419' + nums).length){
					$('#videoShell_409419' + nums).hide();
				}
				$('#dipImage_409419' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_409419' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_409419' + nums).length){
				$('#videoShell_409419' + nums).fadeOut(speed);
			}
			if($('#dipContent_409419' + nums).length){
				$('#dipContent_409419' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_409419' + nums).length){
				$('#nav_409419' + nums).addClass('on');
			}
			
			if($('#nav_li_409419' + nums).length){
				$('#nav_li_409419' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_409419' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_409419' + nums).length){
					$('#videoShell_409419' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_409419' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_409419' + nums).length){
					$('#videoShell_409419' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_409419' + nums).length){
				$('#dipContent_409419' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_409419(timer);
			document.getElementById('currentNum_409419').value = nums;
		}
	}
	
	function slideshowMotion_409419(){
