		searchText();

        // NAVIGATION APP ANIMATIONS
        $(document).off("click", ".navigation div.collapsible");
        $(document).on("click", ".navigation div.collapsible", function () {
            csClosePageSubMenu($(this).parent());
        });
		$(document).off("click", ".navigation div.expandable");
        $(document).on("click", ".navigation div.expandable", function () {
            csOpenPageSubMenu($(this).parent());
        });
        $(document).off("click", ".navigationgroup.collapsible > a");
        $(document).on("click", ".navigationgroup.collapsible > a", function () {
            csClosePageSubMenu($(this).parent());
        });
		$(document).off("click", ".navigationgroup.expandable > a");
        $(document).on("click", ".navigationgroup.expandable > a", function () {
            csOpenPageSubMenu($(this).parent());
        });

		$("div.ui-widget.app.navigation li div.bullet").click(function(){
			return true;
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.schoollist").unbind("hover");
		$(".sw-mystart-dropdown.schoollist").hover(function(){

			},function(){
			$(".sw-dropdown-arrow",this).removeClass("open");
			$(this).removeClass("open");
			$(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden", "true");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.translate").unbind('hover');
		$(".sw-mystart-dropdown.translate").hover(function(e){

		}, function(e) {
			//e.stopPropagation();
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$(this).removeClass("open");
			$(".sw-dropdown").slideUp("slow", "swing").attr("aria-hidden", "true");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$("#sw-mystart-account").unbind('hover');
		$("#sw-mystart-account").hover(function(){

		}, function() {
			$(".icon-font-arrow",this).removeClass("open");
			$("#cs-sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden", "true");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-item ul").stop(true, true);
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

        $(".ui-widget.app.navigation").csAccessibleNavigationApp();

        padSiteName();

	});

	$(function(){

		$(window).bind("resize", function(){ jsMediaQueries();csVideoResize();padSiteName() });



			var additionalHeaders = "", additionalLinks = "";

            //MENU TRANSLATE LANGUAGES
