			pageURL = encodeURIComponent(pageURL);
            var menuTranslateLang = [
            	["Spanish","http://translate.google.com/translate?u=" + pageURL + "&langpair=en|es&hl=en&anno=0&ie=UTF8"],
               	["Chinese","http://translate.google.com/translate?u=" + pageURL + "&langpair=en|zh-CN&hl=en&anno=0&ie=UTF8"],
                ["More","http://translate.google.com/translate?u=" + pageURL + "&langpair=en|pt-CN&hl=en&anno=0&ie=UTF8"]
            ];
            for(var i = 0; i < 3; i++) {
				additionalLinks += "1, " + menuTranslateLang[i][0] + ", " + menuTranslateLang[i][1] + ", _self;";
			}
            additionalHeaders += "Translate;";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
