			        "siteID"                : "4",
                    "allLoaded"             : function(){ }
			});


			$("#gb-global-icons").creativeIcons({
			        "iconNum"      : "3", // NUMBER OF ICONS
			        "icons"         : [
                            ["Communication/21.png", "Contact", "https://www.walkercountyschools.com/domain/123", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["Classroom/4.png", "School Directory", "https://www.walkercountyschools.com/Page/2577", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["Classroom/13.png", "iNow", "https://sis-walkerco.chalkableinformationnow.com/InformationNow/Login.aspx?ReturnUrl=%2fInformationNow", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["Information Technology/8.png", "Schedules", "#", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                    		["Classroom/13.png", "iNow", "https://sis-walkerco.chalkableinformationnow.com/InformationNow/Login.aspx?ReturnUrl=%2fInformationNow", "_blank"] // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                    ],
			        "siteID"       : "4",
			        "siteAlias"    : "al02210233",
			        "calendarLink" : "/Page/2",
			        "contactEmail" : "wcboe@wcslive.com",
			        "allLoaded"    : function(){ 
                    	$(".global-icon img").attr("alt", "");
                        $(".global-icon").each(function(){
                        	var iconText = $(".text",this).text();
                        	$(".text",this).prependTo(this);
                            $(".icon", this).attr("aria-label", iconText);



                        });
                        $(".global-icon").hover(function(){
                        		$(".text", this).addClass("open");
                                },function(){
                                $(".text", this).removeClass("open");
                        	});   

                    }
			});

            $(function() {
                $.schoolListMinibase({
                    'miniBaseDomainID': 4,
                    'miniBaseModuleInstanceID': 124,
                    'cache': true,
                    'schoolListReady': function(schoolList) {
                        /*
                        The returned value is an array of objects and each object is a school record.
                        The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                        */

                        var schools = '';

                        for(var i in schoolList) {
                            if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                                schools += '
' + schoolList[i].schoolName + '';
                            }
                        }

                        $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                    }
                });
            });


		buildStyleSheet();

		useChannelForSection();
		dynamicContent();
        jsMediaQueries();
		modEvents();
        //searchControls();
        slideshow();
        animateTranslate();
        animateSearch();
        accessibility();
	});

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 

                //GLOBAL ICON  and WATERMARK POSITIONING
                var contentWidth = $("#gb-header").width();
                var windowWidth = $(window).width();
                var giOffset = (windowWidth - contentWidth)/2 + 40;
                $("#gb-global-icons").css("right",giOffset);
                $("#hp-watermark").css("right",giOffset);
                $(window).resize(function() {
                    var contentWidth = $("#gb-header").width();
                    var windowWidth = $(window).width();
                    var giOffset = (windowWidth - contentWidth)/2;
                    $("#gb-global-icons").css("right",giOffset);
                    $("#hp-watermark").css("right",giOffset);
                });
                $(window).scroll(marginTop);
			break;
			case "768":
				//appView()
			break;
			case "640":
				//appView();
			break;
			case "480":
				//appView();
			break;
			case "320":
				//appView();
			break;
		}
	}

    function padSiteName() {
    	switch(csGetBreakPoint()) {
			case "desktop":
            var logoCheck = "true";
            if(logoCheck == "true") {
				var logoWidth = $("#gb-logo").width() + 65;
            	$("#gb-sitename").css("padding-left", logoWidth);
            	$("#gb-channel-list").css("padding-left", logoWidth);
                $("#gb-logo").css({
                    "visibility": "visible"
                });
            }
			break;
            case "768": case "640": case "480":
            var logoCheck = "true";
            if(logoCheck == "true") {
				var logoWidth = $("#gb-logo").width() + 10;
            	$("#gb-sitename").css("padding-left", logoWidth);
            	$("#gb-channel-list").css("padding-left", logoWidth);
                $("#gb-logo").css({
                    "visibility": "visible"
                });
            }    
			break;
			case "320":
				$("#gb-sitename").css("padding-left", "0px");
            	$("#gb-channel-list").css("padding-left", "0px");
			break;
		}
    }

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		if(!$("#hp-content-main div.ui-widget.app.active").length){
			$("#hp-content-main div.ui-widget.app:first").addClass("active");
		}

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content-main div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("#hp-content-main div.ui-widget-header").off("click").click(function(){
			$(this).parent().toggleClass("active");
            $(this).next("div.ui-widget-detail").slideToggle("slow");
		});
	}	

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

    function animateSearch() {
        $("#gb-search .icon-font-search").click(function(){
        	$("#gb-search .gb-search.inner").toggleClass("open");
            $(this).toggleClass("open").toggleClass("closed");
            $(this).attr("aria-label", ($(this).attr("aria-label") == "Open Search") ? "Close Search" : "Open Search");
            $("#sw-search-input").attr("tabindex", ($("#sw-search-input").attr("tabindex") == "-1") ? "0" : "-1");
        });
    }    

    function animateTranslate() {
    	$("#gb-translate .icon-font-translate").click(function(){
        	$("#gb-translate .gb-translate.inner").toggleClass("open");
            $(this).toggleClass("open").toggleClass("closed");
            $(this).attr("aria-label", ($(this).attr("aria-label") == "Open Translate Menu") ? "Close Translate Menu" : "Open Translate Menu");
            $(".rs-slideout-list").attr("aria-hidden", ($(".rs-slideout-list").attr("aria-hidden") == "true") ? "false" : "true");
            $(".rs-slideout-list .spanish a").attr("tabindex", ($(".spanish a").attr("tabindex") == "-1") ? "0" : "-1");
            $(".rs-slideout-list .chinese a").attr("tabindex", ($(".chinese a").attr("tabindex") == "-1") ? "0" : "-1");
            $(".rs-slideout-list .more a").attr("tabindex", ($(".more a").attr("tabindex") == "-1") ? "0" : "-1");
        });
    }
    function slideshow(){

        var mediaChoice = "Multimedia_Gallery";
        if(mediaChoice == "Multimedia_Gallery"){
            if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1500,
                    "imageHeight" : 915,
                    "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : true,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {
                    	$(".mmg-description-outer").animateAuto("height", 1000);
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {

                            var contentWidth = $("#gb-header").width();
                            var windowWidth = $(window).width();
                            var giOffset = (windowWidth - contentWidth)/2 + 40;
                            $("#hp-slideshow .mmg-description-outer").css("right",giOffset);
                            $("#hp-slideshow .mmg-control.play-pause").insertAfter("#hp-slideshow .mmg-control.back");
                            $("#hp-slideshow .mmg-control").wrapAll('
');
                            $("#hp-slideshow .mmg-control-outer").appendTo('#hp-slideshow .mmg-description-outer');
                            $("#hp-slideshow .mmg-description-outer").append('
')

                    		//MOD DESCRIPTION 
                    		$(".mmg-description-outer").animateAuto("height", 0);

                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {
                    		var contentWidth = $("#gb-header").width();
                            var windowWidth = $(window).width();
                            var giOffset = (windowWidth - contentWidth)/2 + 40;
                            $("#hp-slideshow .mmg-description-outer").css("right",giOffset);
                    } // props.element, props.mmgRecords
                });
            }
        } else {
        	$("#mmg-player").remove();
        }
    }

