                case "640": case "480": case "320":
                  var newCalc = (($body.scrollTop() / scrollOffset) * 6) - 41;
                  $slideshowTriangleBot.css({
                      '-moz-transform': 'rotate(' + calc + 'deg)',
                      '-o-transform': 'rotate(' + calc + 'deg)',
                      '-webkit-transform': 'rotate(' + calc + 'deg)',
