		//GET SCHOOLWIRES FOOTER LINKS
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var swURL = $("#sw-footer-logo a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	
		$("#sw-logo a").attr("href", swURL);			
	}

    var marginTop = function() {

        var getWidth = $(window).width();
        var headerHeight = $("#sticky-header").height();
        var emergencyHeight = $("#gb-emergency-outer").height();

		if (getWidth > 768) { // 960+
			if($(window).scrollTop() >= emergencyHeight && scrollLock == 0) {
				$("#sticky-header").addClass("fixed");
        		$("main").css("margin-top",headerHeight);

                scrollLock = 1;

			} else if($(window).scrollTop() < emergencyHeight && scrollLock == 1) {
				$("#sticky-header").removeClass("fixed");
                $("main").css("margin-top", "0px");
                scrollLock = 0;
			}
		} else {
        	$("#sticky-header").removeClass("fixed");
        	$("main").css("margin-top", "0px");
        }

    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}


	function modEvents(){
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "yes"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};

            if($('.upcoming-column.left h1', this).length){
                var newDateTime = $('.upcoming-column.left h1', this).html().split("");
                var newDate = '';

                newDate += newDateTime[0] + "";
                //$(".upcoming-column.right", this).prepend(newDate);
                //$(".ui-article-description", this).prepend(newDate);
                //$(".upcoming-column.right .ui-article-description", this).each(function(){
                //	$(this).prepend(newDate);
                //});
            }    

            //add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				} else {
                	$(this).prepend(newDate);
                }
			});  

            //REMOVE ALL INSTANCES OF :00 in UPCOMING EVENT TIMES
            $("div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.right .ui-article-description").each(function(){
                var time = $(".sw-calendar-block-time", this).text().replace(/\:00/g,"");
                $(".sw-calendar-block-time", this).text(time);
            });

		});
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function buildStyleSheet(){
		var secondaryColor90 = "rgba(" + hexToRgb('#272653') + ", .9)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	' .sw-channel-item ul.sw-channel-dropdown {' +
					'	background: ' + secondaryColor90 + ';' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

