                // Override addClass
                (function () {
                    var originalAddClassMethod = jQuery.fn.addClass;

                    jQuery.fn.addClass = function () {
                        // Execute the original method.
                        var result = originalAddClassMethod.apply(this, arguments);

                        // trigger a custom event
                        jQuery(this).trigger('cssClassChanged');

                        // return the original result
                        return result;
                    }
                })();

                // Override removeClass
                (function () {
                    var originalRemoveClassMethod = jQuery.fn.removeClass;

                    jQuery.fn.removeClass = function () {
                        // Execute the original method.
                        var result = originalRemoveClassMethod.apply(this, arguments);

                        // trigger a custom event
                        jQuery(this).trigger('cssClassChanged');

                        // return the original result
                        return result;
                    }
                })();

                $(document).ready(function () {
                    $('img[title="Click here to log out"]').click(function () {
                        Set_Cookie('kpbsd_loggedOut', 'true', '', '/', '', '');
                    });

                    $('embed, object').attr('wmode', 'transparent').css('z-index', '1');

                });

                jQuery(document).on('click', '.mega-dropdown', function (e) {
                    e.stopPropagation();
                });

                $('li.mega-dropdown').bind('cssClassChanged', function () {
                    if ($(this).hasClass('open')) {
                        $('#sub').addClass('faded-focus');
                    } else {
                        $('#sub').removeClass('faded-focus');
                    }
                });

