var tweetWindow = function(url, text) {
	var leftPosition, topPosition;
    //Allow for borders.
    leftPosition = (window.screen.width / 2) - 290;

	//Allow for title and status bars.
    topPosition = (window.screen.height / 2) - 229;

	//Open the window.
  	window.open( "http://twitter.com/share?url=" + 
    encodeURI(url) + "&text=" + encodeURIComponent(text) + "&count=none/", "tweet", "height=358,width=560,resizable=1,left="+ leftPosition + ",top=" + topPosition + ",screenX=" + leftPosition + ",screenY="+ topPosition + ",toolbar=no,menubar=no,scrollbars=no,location=no,directories=no"); 
}

