 $(document).click(function (event) {
	var target = $(event.target);

	if (target.attr('src') && target.parents('.image').length && target.parents('.widget').length) {
		var text = target.attr('title');
		
		if (!text.length) {
			text = "N/A";
		}

		gtag('event', 'Image - ' + text,
			{
				event_category: 'Image',
				event_label: window.location.href
			});
	}

	if (target.is('button') || target.hasClass('button') || target.parents().hasClass('button')) {

		var text = "";

		if (target.parents('.button')[0]) {
			text = target.parents('.button')[0].text;
		} else if (target.text().length) {
			text = target.text();
		} else if (target.attr('title').length){
			text = target.attr('title');
		}

		if (!text.length) {
			text = "N/A";
		}

		gtag('event', 'Button - ' + text,
			{
				event_category: 'Button',
				event_label: window.location.href
			});
	}

	if (target.parents('.widgetCustomHtml').length) {
		gtag('event', 'Custom Html Clicked',
			{
				event_category: 'Custom Html',
				event_label: window.location.href
			});
	}

	if (target.parents('.editor').length) {
		gtag('event', 'Editor Link Clicked',
			{
				event_category: 'Editor',
				event_label: window.location.href
			});
	}

	if (target.parents('.GraphicLinks').length) {

		var text = "";

		var targetGraphicLink = target;

		if (target.hasClass('widgetGraphicLinksLink')) {
			targetGraphicLink = $(target.children()[0]);
		}

		if (targetGraphicLink.hasClass('text')) {
			text = targetGraphicLink.text();
		} else if (targetGraphicLink.attr('src').length) {
			if (targetGraphicLink.attr('alt').length) {
				text = targetGraphicLink.attr('alt');
			} else {
				text = targetGraphicLink.attr('src');
			}
		} else {
			text = "N/A";
		}


		gtag('event', 'Graphic Link - ' + text,
			{
				event_category: 'Graphic Links',
				event_label: window.location.href
			});
	}

	if (target.parents('.InfoAdvanced').length) {
		gtag('event', 'Info Advanced Clicked',
			{
				event_category: 'Info Advanced',
				event_label: window.location.href
			});
	}

	if (target.parents('.list').length) {
		gtag('event', 'List Clicked',
			{
				event_category: 'List',
				event_label: window.location.href
			});
	}
	
	if ((target.parents('.megaMenuItem').length && (target.parents('#widgetPage3f8b07cd-3a2a-460b-b812-567c5341a6ec').length || target.parents('#widgetPage7aae4bed-f21d-4bbf-a84f-3ac51a4ca1df').length || target.parents('#widgetPage1543e95a-f81d-43b8-9a64-835e68dd09f3').length)) || (target.parents('.topMenuItem').length && target.parents('#stickyStructuralContainer_b74d5db8-93a5-435c-99e0-a0defbe1c23d').length)) {
