		var actionText = '';
		var actionsArray = [];

		if (megaMenuText === '') { megaMenuText = null; }
		if (subTitleText === '') { subTitleText = null; }
		if (targetText === '') { targetText = null; }

		if (megaMenuText) { actionsArray.push(megaMenuText); }
		if (subTitleText) { actionsArray.push(subTitleText); }
		if (targetText) { actionsArray.push(targetText); }

		if (actionsArray.length > 1) {
			for (i = 0; i < actionsArray.length - 1; i++) {
				cur = actionsArray[i];
				next = actionsArray[i+1];
				if (cur === next) {
					actionsArray[i] = null;
				}
			}
			actionsArray = actionsArray.filter(Boolean);
		}
		else if (actionsArray.length < 1) {
			actionsArray = ['N/A'];
		}

		actionText = actionsArray.join(' > ').replace("Opens in New Window", "").trim();		

		gtag('event', 'Mega Menu : ' + actionText,
