function writeHeader(result) {
  jQuery('#universal_header').html(result.header);
}

(function() {
  var getQueryParam = function(key, uri) {
    var href = uri ? uri : window.location.href;
    var reg = new RegExp( '[?&]' + key + '=([^&#]*)', 'i' );
    var string = reg.exec(href);
    return string ? string[1] : null;
  };

  jQuery.get(
    {
      'url': '/gsr/header',
      'dataType': 'jsonp',
      'jsonpCallback': 'writeHeader',
      'cache': true,
      'data': {
        lang: getQueryParam('lang')
      }
    }
  );
})();

jQuery(function () {
    jQuery('.js-connect-with-us-buttons').on({
        mouseenter: function () {
            var cssClass = jQuery(this).attr('class');
            jQuery(this).attr('class', cssClass + '-c');
        },
        mouseleave: function () {
            var cssClass = jQuery(this).attr('class');
            cssClass = cssClass.replace(new RegExp('-c$'), '');
            jQuery(this).attr('class', cssClass);
        }
    }, 'span');
});

