function writeModalCode(result) {
  jQuery('body').append(result.data);
  jQuery('body').on('click', '.multi-select-button-group button', function() {
    GS.multiSelectButtonGroup.apply(this, arguments);
  });

  var viewport = function() {
    var e = window, a = 'inner';
    if (!('innerWidth' in window )) {
      a = 'client';
      e = document.documentElement || document.body;
    }
    return { width : e[ a+'Width' ] , height : e[ a+'Height' ] };
  }

  if(viewport().width >= 768 && !window.location.href.includes('lang=es')) {
    setTimeout(function(){
      if(!GS || !GS.session || !GS.session.isSignedIn || !GS.session.isSignedIn()) {
        var isCcNewsletterModalCookieSet = document.cookie.replace(/(?:(?:^|.*;\s*)_cc_newsletter\s*\=\s*([^;]*).*$)|^.*$/, "$1") === 'true';
        if (!isCcNewsletterModalCookieSet) {
          GS.modal.manager
            .showModal(GS.modal.EmailJoinModal)
            .done(GS.subscription.greatNewsSignUp);
          var expDate = new Date();
          expDate.setTime(expDate.getTime() + (1000 * 60 * 60 * 24));
          Cookies.set('_cc_newsletter', 'true', { path: '/', expires: expDate });
        }
      }
    }, 1000 * 20);
  }
}

function handleGsrAssets(result) {
  window.googletag = window.googletag || {};
  googletag.cmd = googletag.cmd || [];
  window.gon = window.gon || {};
  if(!(window.gon.advertising_enabled === false)) {
    window.gon.advertising_enabled = true;
  }

  var assetMap = result.data;
  var mobileOverlayUri = assetMap['mobile-overlay-ad'];
  var reactReduxUri = assetMap['react-redux'];
  var searchBoxUri = assetMap['search-box']

  var s = document.createElement("script");
  s.type = "text/javascript";
  s.src = mobileOverlayUri;
  jQuery("head").append(s);

  /* s = document.createElement("script"); */
  /* s.type = "text/javascript"; */
  /* s.src = reactReduxUri; */
  /* jQuery("head").append(s); */

  s = document.createElement("script");
  s.type = "text/javascript";
  s.src = searchBoxUri;
  document.body.appendChild(s);

  GS.ad.init();
  googletag.cmd.push(function () {
    GS_renderMobileOverlayAdOnScroll();
  });
}

