function abSubscribeTest() {
    var subscribeRandom =  Math.random();
    var subscribeLink, linkHref, linkText, controlUrl, variableUrl, controlText, variableText, eventName;

    if (TheWeek.isMobile) {
        subscribeLink = jQuery('#subscribe-link-header-mobile');
        //mobile links
        controlUrl = 'https://subscribe.theweek.com/servlet/OrdersGateway?cds_mag_code=TWE&cds_page_id=246023&cds_response_key=I0DRPMSW4';
        variableUrl = 'https://subscribe.theweek.com/servlet/OrdersGateway?cds_mag_code=TWE&cds_page_id=249509&cds_response_key=I0DRPMRW2';
        linkHref = subscribeRandom < 0.5 ? controlUrl : variableUrl;
        linkText = subscribeRandom < 0.5 ? 'Subscribe' : 'Try 6 free';
        eventName = 'Subscribe 50/50 Test MOBILE - 6 Risk-Free Issues vs Annual/Monthly $99/$9.99';
    } else {
        subscribeLink = jQuery('#subscribe-link-header-desktop-tablet');
        // desktop links
        controlUrl = 'https://subscribe.theweek.com/servlet/OrdersGateway?cds_mag_code=TWE&cds_page_id=246023&cds_response_key=I0DRPKSW4';
        variableUrl = 'https://subscribe.theweek.com/servlet/OrdersGateway?cds_mag_code=TWE&cds_page_id=249509&cds_response_key=I0DRPKRW2';
        linkHref = subscribeRandom < 0.5 ? controlUrl : variableUrl;
        linkText = subscribeRandom < 0.5 ? 'Subscribe & save' : 'Try 6 issues free';
        eventName = 'Subscribe 50/50 Test DESKTOP - 6 Risk-Free Issues vs Annual/Monthly $99/$9.99';
    }

    subscribeLink.text(linkText);
    subscribeLink.attr({
        'href': linkHref,
        'onclick': 'TheWeek.pushUAEvent("Testing", "' + eventName + '", "Control - Clicked");'
    });

    TheWeek.pushUAEvent('Testing', eventName, 'Control - Seen', 0, {nonInteraction: true});
}

