// Search submit
function searchSubmit() {
    var term = document.getElementById("search_input").value;
    // term = term.replace(' ', '+');
    if(term!=""){
        document.location.href = "/search/"+term;
    }
    else{
        document.location.href = "/search";
    }
    return false;
}
jQuery("#search_input").keyup(function (e) {
    if (e.keyCode == 13) {
        e.preventDefault();
        searchSubmit();
    }
});

