if (Cookies.get('LoggedIn') == "True") {
    jQuery("#login-footer").text("Subscriber logout");
    jQuery("#login-footer").attr("href","/");
    jQuery("#login-footer").click( function(e){
        e.preventDefault();
        Cookies.remove('LoggedIn');
        window.location.href = '/';
    });
}

jQuery(document).ready(function() {
    var moreAboutMenu = jQuery('.footer-links-section');
    var moreAboutMenuToggler = jQuery('.footer-links-set .more-about');

    function openCloseMenu(elem) {
        var menu = jQuery(elem);

        menu.toggleClass('menu-open', !menu.hasClass('menu-open'));
    }

    moreAboutMenuToggler.click(function() {
        openCloseMenu(moreAboutMenu);
    });
});
